mapper.select:
select b.boardId, b.title, b.content, b.create_date, b.update_date, u.user_id, u.nickname
from board b
       left join user u on b.user_id = u.user_id;

mapper.update:
update board
set title       = #{title},
    content     = #{content},
    update_date = #{updateDate},
    user_id     = #{userId}
where board_id = #{boardId}

mapper.select:
select exists(
         select *
         from board
         WHERE board_id = #{boardId}
         )
