mapper.update:
update post
set view_count = (select view_count from post where post_id = #{postId}) + 1
where post_id = #{postId}

update post
set title       = #{title},
    content     = #{content},
    update_date = #{updateDate},
    user_id     = #{userId}
where post_id = #{postId}

mapper.select:
select exists(
         select *
         from post
         where post_id = #{postId}
         )
